import React, { useState } from 'react';

const initialState = {
  lots: 1,
  positionType: '',
  optionType: '',
  expiryType: '',
  selectStrikeCriteria: '',
  simpleMomentum: {
    enabled: false,
    type: '',
    value: 0,
  },
  trailSL: {
    enabled: false,
    type: '',
    value: {
      instrumentMove: 0,
      stopLossMove: 0,
    },
  },
};

const App = () => {
  const [forms, setForms] = useState([initialState]);

  const handleAddLeg = () => {
    setForms((prevForms) => [...prevForms, { ...initialState }]);
  };

  const handleInputChange = (e, index) => {
    const { name, value, type, checked } = e.target;
    setForms((prevForms) => {
      const updatedForms = [...prevForms];
      if (type === 'checkbox') {
        updatedForms[index] = {
          ...updatedForms[index],
          [name]: {
            ...updatedForms[index][name],
            enabled: checked,
          },
        };
      } else {
        updatedForms[index] = {
          ...updatedForms[index],
          [name]: value,
        };
      }
      return updatedForms;
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log(forms);
  };

  return (
    <div>
      {forms.map((form, index) => (
        <form key={index} onSubmit={handleFormSubmit}>
          <div>
            <label>
              Total lot:
              <input
                type="number"
                name="lots"
                value={form.lots}
                onChange={(e) => handleInputChange(e, index)}
              />
            </label>
          </div>
          <div>
            <label>
              Position Type:
              <input
                type="text"
                name="positionType"
                value={form.positionType}
                onChange={(e) => handleInputChange(e, index)}
              />
            </label>
          </div>
          <div>
            <label>
              Option Type:
              <input
                type="text"
                name="optionType"
                value={form.optionType}
                onChange={(e) => handleInputChange(e, index)}
              />
            </label>
          </div>
          <div>
            <label>
              Expiry:
              <input
                type="text"
                name="expiryType"
                value={form.expiryType}
                onChange={(e) => handleInputChange(e, index)}
              />
            </label>
          </div>
          <div>
            <label>
              Select Strike Criteria:
              <input
                type="text"
                name="selectStrikeCriteria"
                value={form.selectStrikeCriteria}
                onChange={(e) => handleInputChange(e, index)}
              />
            </label>
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name={`simpleMomentum.enabled`}
                checked={form.simpleMomentum.enabled}
                onChange={(e) => handleInputChange(e, index)}
              />
              Simple Momentum
            </label>
          </div>
          <div>
            <label>
              Type:
              <input
                type="text"
                name={`simpleMomentum.type`}
                value={form.simpleMomentum.enabled ? form.simpleMomentum.type : ''}
                onChange={(e) => handleInputChange(e, index)}
                disabled={!form.simpleMomentum.enabled}

                />
            </label>
          </div>
          <div>
            <label>
              Value:
              <input
                type="number"
                name={`simpleMomentum.value`}
                value={form.simpleMomentum.enabled ? form.simpleMomentum.value : ''}
                onChange={(e) => handleInputChange(e, index)}
                disabled={!form.simpleMomentum.enabled}
              />
            </label>
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name={`trailSL.enabled`}
                checked={form.trailSL.enabled}
                onChange={(e) => handleInputChange(e, index)}
              />
              Trail SL
            </label>
          </div>
          <div>
            <label>
              Type:
              <input
                type="text"
                name={`trailSL.type`}
                value={form.trailSL.enabled ? form.trailSL.type : ''}
                onChange={(e) => handleInputChange(e, index)}
                disabled={!form.trailSL.enabled}
              />
            </label>
          </div>
          <div>
            <label>
              Value (Instrument Move):
              <input
                type="number"
                name={`trailSL.value.instrumentMove`}
                value={form.trailSL.enabled ? form.trailSL.value.instrumentMove : ''}
                onChange={(e) => handleInputChange(e, index)}
                disabled={!form.trailSL.enabled}
              />
            </label>
          </div>
          <div>
            <label>
              Value (Stop Loss Move):
              <input
                type="number"
                name={`trailSL.value.stopLossMove`}
                value={form.trailSL.enabled ? form.trailSL.value.stopLossMove : ''}
                onChange={(e) => handleInputChange(e, index)}
                disabled={!form.trailSL.enabled}
              />
            </label>
          </div>
          <div>
            <button type="submit">Submit</button>
          </div>
        </form>
      ))}
      <div>
        <button onClick={handleAddLeg}>Add Leg</button>
      </div>
    </div>
  );
};

export default App;
